site.headStyle:
html, body { scroll-behavior: smooth; }

.hero-scroll-down {
  cursor: pointer;
}

@media (min-width:480px) {
  body div.container-element.section-element.hero {
    height: 100vh;
  }
}
@media (min-width: 1260px) {
  body div.container-element.section-element.hero {
    border: 30px solid white;
    box-sizing: border-box;
  }
}
div.image .text-element {
  position: absolute;
  top: 20px;
  bottom: 20px;
  left: 20px;
  right: 20px;
  width: initial;
  height: initial;
}

.silex-runtime div.image .text-element {
  top: 0;
  bottom: 0;
  left: 0;
  right: 0;
}

@media (min-width:480px) {
  div.image .text-element {
    background-color: rgba(255, 255, 255, .6);
  }

  div.image .silex-element-content {
    display: flex;
    flex-direction: column;
    justify-content: center;
    height: 100%;
  }

  .silex-runtime div.image .text-element {
    opacity: 0;
    transition: opacity .3s ease;
  }

  .silex-runtime div.image:hover .text-element {
    opacity: 1;
  }
}
